mapper.select:
select * from( select ROWNUM rnum,sub2.* from ( select sub.*, member.profile_img from ( select post.*,category.cid as cid, category.cname as cname from post join percategory on post.pcid = percategory.pcid join category on percategory.cid = category.cid
where cname = #{cname}
) sub join member on sub.id=member.id) sub2)
WHERE rnum >= ((#{page} - 1) * #{perPage} + 1) AND rnum <= (#{page} * #{perPage})

mapper.update:
UPDATE post 
SET title=#{title}, content=#{content}, regdate=default, id=#{id}, pcid=#{pcid}, thumbnail=#{thumbnail}
WHERE pid=#{pid}

UPDATE post 
SET viewcount = #{viewcount}
WHERE pid=#{pid}
